function convertToSlug(text) {
  return text
    .toLowerCase()
    .normalize('NFD')
    .replace(/[\u0300-\u036f]/g, '') // Loại bỏ các dấu thanh và ký tự đặc biệt
    .replace(/đ/g, 'd')  // Thêm quy tắc thay thế cho chữ "đ"
    .replace(/Đ/g, 'd')  // Thêm quy tắc thay thế cho chữ "Đ"
    .replace(/[^\w\s]/gi, '')
    .replace(/\s+/g, '-');
}
